export type AlignSelf = "flex-start" | "flex-end" | "center" | "stretch";
export type AlignItems = "flex-start" | "flex-end" | "center" | "stretch";
export type JustifyContent =
  | "flex-start"
  | "flex-end"
  | "center"
  | "space-between"
  | "space-around";

export type BorderStyle = "dotted" | "dashed" | "solid" | undefined;
export type FlexDirection = "row" | "row-reverse" | "column";
export type FlexWrap = "wrap";
export type Position = "absolute" | "relative";
export type Overflow = "hidden" | "visible" | "scroll";
